mapper.select:
select id, title, sell_point, price, num, barcode, image, cid, status, created, updated
from tb_item
where id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE tb_item ti
SET ti.status = 3, ti.updated = NOW()
WHERE ti.id IN
(
	
		#{id}
	
)

UPDATE tb_item ti
SET ti.status = 2, ti.updated = NOW()
WHERE ti.id IN
(
	
		#{id}
	
)

UPDATE tb_item ti
SET ti.status = 1, ti.updated = NOW()
WHERE ti.id IN
(
	
		#{id}
	
)
